"""
文件作用：数据预处理
method: 将json文件中没有用到的字段去除，只留下需要的数据，降低数据读入的时间
details: 保留原始的content和summary字段;将最相关的问题的回复拼接到后面
"""
import os
import pickle
import json


def data(input_folder="./source",output_folder="./datasets"):
    """
    :param input_folder:包含原始输入数据的文件夹，文件名命名为train.json,valid.json,test.json(赛题中的原始数据）
    :param output_folder:输出的文件夹,文件命名默认为train_.json,valid_.json,test_.json
    :return:
    """
    valid=[json.loads(line) for line in open(os.path.join(input_folder,"valid.json"), 'r',encoding='utf-8')]
    train=[json.loads(line) for line in open(os.path.join(input_folder,"train.json"), 'r',encoding='utf-8')]
    test=[json.loads(line) for line in open(os.path.join(input_folder,"test.json"), 'r',encoding='utf-8')]

    if not os.path.exist(output_folder): #如果不存在则手动创建
        os.makedirs(output_folder)

    with open(os.path.join(output_folder,"train_.json"),'w') as f:
        for line in train:
            f.write(json.dumps({"content":line['content'],"summary":line['summary']+line['retrieval']['search_qa'][0]['answer']})+'\n')

    with open(os.path.join(output_folder, "valid_.json"), 'w') as f:
        for line in valid:
            f.write(json.dumps({"content": line['content'], "summary": line['summary']}) + '\n')

    with open(os.path.join(output_folder, "test_.json"), 'w') as f:
        for line in test:
            f.write(json.dumps({"content": line['content'], "summary": ""}) + '\n')  # test数据集中没有summary字段，手动设置为空



